import React from 'react'

const ItemDetail = ({item}) => {

    const mostraritems = () => {
        detalles = (`<p>Cartel de ${item.itemName} de ${item.itemDescription}</p>
        <p>Precio: ${item.itemPrice}</p>}`)
    }

    return (
        <div>
            <div id = "BoxMostrarDetalles">{detalles}</div>
            <button onClick = {mostraritems}>Detalles del producto</button>
        </div>
  )
};

export default ItemDetail;
