import {React, useContext, useState} from 'react';
import {CartContext} from '../CartContext/CartContext';
import './carrito.css';


const Carrito = () => {

  const {carrito, removeItem} = useContext(CartContext);
  const [items, setItems] = useState([]);

  useEffect(() => {
    const itemsPromise =  new Promise ((resolve, reject) => {
      setTimeout(() => {
        resolve(carrito)
      }, 500)
    })
    itemsPromise 
    .then((res) => {setItems(res);
      
    })
    .catch((error) => {
      console.log(error)
    })

  },[carrito]);

  return (
    <div>
      <h1>TU CARRITO</h1>

      <div className = "ContainerCarrito">
        {
        carrito.map((carrito) => (

          <div className = "ContainerDetallesCarrito">
            
            <img className = "img"src = {carrito.foto} alt = {carrito.nombre} />
            <div>
              <div>Nombre:</div> 
                <div>{carrito.nombre}</div>
              </div>
            <div>
              <div>Cantidad:</div>
              <div>{carrito.cantidad}</div>
            </div>
            <div>
              <div>Tamaño:</div>
              <div>{carrito.size}</div>
            </div>
            <div>
              <div>Color:</div> 
              <div style={{color: `${carrito.color}`}}>■</div>
            </div>
            <div>
              <div>Precio:</div> 
              <div>{carrito.precio}</div>
            </div>
            <button onClick = {removeItem}>Borrar</button>
          </div>
        ))
        }
      </div>

  </div>
  ) 


}

export default Carrito;
