import React from 'react';
import ItemCount from '../../components/ItemCount/ItemCount';
import '../Item/Item.css';
import ItemDetail from '../ItemDetail/ItemDetail'

const Item = ({item}) => {

    const add = (counter) =>{
      if (counter > 0){
        alert(`Se agregaron ${counter} productos.`);
      }
    };  

  return (
    <div className = "box">
      <div className = "ItemBox">
          
          <img className = "img"src = {item.pictureUrl} alt = {item.ItemName} />
          <div className = "title">{item.itemName}</div>
          <div>Id: {item.itemId}</div>
          <div>Precio: {item.itemPrice}</div>
          <button className = "BotonDetalle" onClick={<ItemDetail/>}>Detalles del producto</button>
          <ItemCount
          stock = "5"
          initial = "1"
          add = {add}     
          />
      </div>
    </div>
  )
}

export default Item;
