import { createContext } from 'react/cjs/react.development';

export const CartContext = createContext();

const carrito = [];  

export const ItemsProvider = ({children}) =>{
  
    const addItem = (nombreitem,  cantidad) =>{
        let carritonombre = carrito.filter(carrito => carrito.nombre === `${nombreitem}`);
        if (nombreitem === carritonombre.nombre){
            console.log("filtró por nombre");
        }
        carrito.push(`{nombre: ${nombreitem}, cantidad: ${cantidad}}`);
        console.log(`carrito: ${carrito}`);

    }
    
    return <CartContext.Provider value = {addItem}>
        {children}

    </CartContext.Provider>;
}
