import React, {useState}from 'react';
import ItemCount from '../../components/ItemCount/ItemCount';
import '../Item/Item.css';

const Item = ({item}) => {

  const [show, setShow] = useState(false);

    const add = (counter) =>{
      if (counter > 0){
        alert(`Se agregaron ${counter} productos.`);
      }
    };  

    return (
      <div className = "box">
        <div className = "ItemBox">
            
            <img className = "img"src = {item.pictureUrl} alt = {item.ItemName} />
            <div className = "title">{item.itemName}</div>
            <div>Id: {item.itemId}</div>
            {show ?(
              <div className = "BoxDetails">       
                  <div>
                      <div className = "BoxListas">
                          <div>Tamaños:</div>
                          <ul className = "ListaTamaños">
                            <li><input type= "radio" name = "tamaños"></input><label>{item.itemSize[0]}</label></li>
                            <li><input type= "radio" name = "tamaños"></input><label>{item.itemSize[1]}</label></li>
                            <li><input type= "radio" name = "tamaños"></input><label>{item.itemSize[2]}</label></li>
                            <li><input type= "radio" name = "tamaños"></input><label>{item.itemSize[3]}</label></li>
                            <li><input type= "radio" name = "tamaños"></input><label>{item.itemSize[4]}</label></li>
                          </ul>
                      </div>
                      <div>Precio: {item.itemPrice}</div>
                      <div className = "BoxListas">
                          <div>Colores:</div>
                          <ul className = "ListaColores">
                              <li><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[0]}`}}>■</label></li>
                              <li><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[1]}`}}>■</label></li>
                              <li><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[2]}`}}>■</label></li>
                              <li><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[3]}`}}>■</label></li>
                              <li><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[4]}`}}>■</label></li>
                              <li><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[5]}`}}>■</label></li>
                              <li><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[6]}`}}>■</label></li>
                              <li><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[7]}`}}>■</label></li>
                          </ul>
                      </div>
                  </div>
                </div>  
            ):(<div></div>)}
            <button className = "BotonDetalle" onClick={() => {setShow(!show);}}>Detalles del producto{show ? 'BoxDetails' : ''}</button>
            <ItemCount stock = "5" initial = "1" add = {add}/>
        </div>
    </div>
  )
}

export default Item;
