import React, {useState}from 'react';
import ItemCount from '../../components/ItemCount/ItemCount';
import '../Item/Item.css';

const Item = ({item, show}) => {

    const add = (counter) =>{
      if (counter > 0){
        alert(`Se agregaron ${counter} productos.`);
      }
    };  

    return (
      <div className = "box">
        <div className = "ItemBox">
            
            <img className = "img"src = {item.pictureUrl} alt = {item.ItemName} />
            <div className = "title">{item.itemName}</div>
            <div>Id: {item.itemId}</div>
            <button className = "BotonDetalle" onClick={() => {setShow(!show);}}>Detalles del producto{show ? 'BoxDetails' : ''}</button>
            <ItemCount stock = "5" initial = "1" add = {add}/>
        </div>
    </div>
  )
}

export default Item;
