import React, {useState} from 'react'
import Item from '../Item/Item'
import ItemCount from '../ItemCount/ItemCount'

const ItemDetail = ({item}) => {
    
    const [show, setShow] = useState(false);

    const add = (counter) =>{
        if (counter > 0){
          alert(`Se agregaron ${counter} productos.`);
        }
      };  


return(
    <div>
        {show ?(
              <div className = "BoxDetails">       
                  <div>
                  <div className = "BoxListas">{item.itemDescription}</div>
                      <div className = "BoxListas">
                          <div>Tamaños:</div>
                          <ul className = "ULista">
                            <li className = "Li"><input type= "radio" name = "tamaños"></input><label>{item.itemSize[0]}</label></li>
                            <li className = "Li"><input type= "radio" name = "tamaños"></input><label>{item.itemSize[1]}</label></li>
                            <li className = "Li"><input type= "radio" name = "tamaños"></input><label>{item.itemSize[2]}</label></li>
                            <li className = "Li"><input type= "radio" name = "tamaños"></input><label>{item.itemSize[3]}</label></li>
                            <li className = "Li"><input type= "radio" name = "tamaños"></input><label>{item.itemSize[4]}</label></li>
                          </ul>
                      </div>
                      <div>Precio: {item.itemPrice}</div>
                      <div className = "BoxListas">
                          <div>Colores:</div>
                          <ul className = "ULista">
                              <li className = "Li"><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[0]}`}}>■</label></li>
                              <li className = "Li"><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[1]}`}}>■</label></li>
                              <li className = "Li"><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[2]}`}}>■</label></li>
                              <li className = "Li"><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[3]}`}}>■</label></li>
                              <li className = "Li"><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[4]}`}}>■</label></li>
                              <li className = "Li"><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[5]}`}}>■</label></li>
                              <li className = "Li"><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[6]}`}}>■</label></li>
                              <li className = "Li"><input type= "radio" name = "color"></input><label style={{color: `${item.itemColors[7]}`}}>■</label></li>
                          </ul>
                      </div>
                  </div>
                </div>  
            ):(<div></div>)}
            <button className = "BotonDetalle" onClick={() => {setShow(!show);}}>Detalles del producto{show ? 'BoxDetails' : ''}</button>
            <ItemCount stock = "5" initial = "1" add = {add}/>
    </div>
    
)


};

export default ItemDetail;
