import React, {useState}from 'react';
import ItemCount from '../../components/ItemCount/ItemCount';
import '../Item/Item.css';

const Item = ({item}) => {

  const [show, setShow] = useState(false);

    const add = (counter) =>{
      if (counter > 0){
        alert(`Se agregaron ${counter} productos.`);
      }
    };  

    return (
      <div className = "box">
        <div className = "ItemBox">
            
            <img className = "img"src = {item.pictureUrl} alt = {item.ItemName} />
            <div className = "title">{item.itemName}</div>
            <div>Id: {item.itemId}</div>
            {show ?(
              <div className = "BoxDetails">       
                  <div>
                      <div className = "BoxListas">
                          <div>Tamaños:</div>
                          <ul className = "ListaTamaños">
                              <li>{item.itemSize[0]}</li>
                              <li>{item.itemSize[1]}</li>
                              <li>{item.itemSize[2]}</li>
                              <li>{item.itemSize[3]}</li>
                              <li>{item.itemSize[4]}</li>
                          </ul>
                      </div>
                      <div>Precio: {item.itemPrice}</div>
                      <div className = "BoxListas">
                          
                          <ul className = "ListaColores">
                              <div>Colores:</div>
                              <li>{item.itemColors[0]}</li>
                              <li>{item.itemColors[1]}</li>
                              <li>{item.itemColors[2]}</li>
                              <li>{item.itemColors[3]}</li>
                              <li>{item.itemColors[4]}</li>
                              <li>{item.itemColors[5]}</li>
                              <li>{item.itemColors[6]}</li>
                              <li>{item.itemColors[7]}</li>
                          </ul>
                      </div>
                  </div>
                </div>  
            ):(<div></div>)}
            <button className = "BotonDetalle" onClick={() => {setShow(!show);}}>Detalles del producto{show ? 'BoxDetails' : ''}</button>
            <ItemCount stock = "5" initial = "1" add = {add}/>
        </div>
    </div>
  )
}

export default Item;
