import React from 'react';
import './ItemCount.css';
import { useState } from 'react';

const ItemCount = ({stock, initial}) => {
  
  const [contador, ActualizarContador] = useState(initial);
  console.log(contador);
  console.log(ActualizarContador);
  console.log(initial);

  //contador = initial;

  const SubirCantidad = () => {
        ActualizarContador(parseInt(contador) + 1);
  };
  
  const BajarCantidad = () => {
      ActualizarContador(contador - 1);
  };

  
  return <div>
      <div className="ContenedorContador">
        <button id= "BotonContadorMas" onClick={SubirCantidad}>+</button>
        <div id = "Contador">{contador}</div>
        <button id = "BotonContadorMenos" onClick={BajarCantidad}>-</button>
      </div>
      <button id = "BotonAgregar">Agregar al carrito</button>
      
  </div>;
}


export default ItemCount;
