import React from 'react';
import {useContext} from 'react';
import {CartContext} from '../CartContext/CartContext';
import './carrito.css';


const Carrito = () => {

  const {carrito} = useContext(CartContext);

  return (
    <div>
      <h1>TU CARRITO</h1>

      <div className = "ContainerCarrito">
        {
        carrito.map((carrito) => (

          <div className = "ContainerDetallesCarrito">
            
            <img className = "img"src = {carrito.pictureUrl} alt = {carrito.nombre} />
            <div>Nombre: {carrito.nombre}</div>
            <div>Cantidad: {carrito.cantidad}</div>
            <div>Tamaño: {carrito.size}</div>
            <div>
              <div>Color:</div> 
              <div style={{color: `${carrito.color}`}}>■</div>
            </div>
            <div>Precio: {carrito.precio}</div>

          </div>
        ))
        }
      </div>

  </div>
  ) 


}

export default Carrito;
