import React from 'react'
import ItemCount from '../../components/ItemCount/ItemCount';
import Foto1 from '../../imagenes/Imagenes-productos/Foto1.jpg'

const Item = (props) => {

    const Add = (counter) =>{
        alert(`Se agregaron ${counter} productos.`);
      };  

  return (
      
    <div>
        <div>Id: {props.ItemId}</div>
        <img src={`../../imagenes/Imagenes-productos/${props.PictureUrl}`}></img>
        <p>{props.ItemName}</p>
        <p>Cartel de {props.ItemName} de {props.ItemDescription}</p>
        <p>Precio: {props.ItemPrice}</p>
        <button>Detalles del producto</button>
        <ItemCount
        stock = {props.stock}
        initial = {props.initial} 
        Add = {Add}     
        />



    </div>
  )
}

export default Item;
