import { createContext } from 'react/cjs/react.development';

export const CartContext = createContext();

const carrito = [
    {
        nombre: "nombre",
        cantidad: 0
    }
];  

export const ItemsProvider = ({children}) =>{
  
    const addItem = (newItem) =>{

        //let carritonombre = carrito.filter(carro => carro.nombre === newItem.nombre);

        //console.log(`carritonombre = ${JSON.stringify(carritonombre)} newitem: ${newItem.nombre}`)

        //if (newItem.nombre === carritonombre.nombre){
        //    console.log("filtró por nombre");
        //}

        carrito.push(newItem);


    }

    console.log(`carrito: ${JSON.stringify(carrito)}`);

    
    return <CartContext.Provider value = {addItem}>
        {children}

    </CartContext.Provider>;
}
