import { createContext } from 'react/cjs/react.development';

export const CartContext = createContext();

const carrito = [];  

export const ItemsProvider = ({children}) =>{
  
    const addItem = (newItem) =>{

        let carritonombre = carrito.filter(carrito => carrito.nombre === newItem.nombre);

        console.log(`carritonombre = ${carritonombre} newitem: ${newItem.nombre}`)

        if (newItem.nombre === carritonombre.nombre){
            console.log("filtró por nombre");
        }

        carrito.push(newItem);


    }

    console.log(`carrito: ${carrito}`);

    
    return <CartContext.Provider value = {addItem}>
        {children}

    </CartContext.Provider>;
}
