import React from 'react';
import Item from '../../components/Item/Item';
import '../ItemList/ItemList.css';

const ItemList = () => {

  let p =  new Promise ((resolve, reject) => {
    setTimeout(function(){
      resolve("¡Éxito!");
    }, 5000);
  
  })

  return (
    p.then((message) => 
    
    <div className = "ItemListBox">
    <div className = "Item">
      <Item 
      ItemId =  "1"      
      PictureUrl = "Foto1.jpg"
      ItemName = "Kirby"
      ItemDescription = "80cm x 60cm"
      ItemPrice = "$15.000"
      stock = "10"
      initial = "1"
      />
    </div>
    <div className = "Item">
      <Item        
      ItemId =  "2" 
      PictureUrl = "Foto2.jpg"
      ItemName = "Bulbasaur"
      ItemDescription = "70cm x 40cm"
      ItemPrice = "$10.000"
      stock = "5"
      initial = "1"
      />
    </div>
    <div className = "Item">
      <Item        
      ItemId =  "3" 
      PictureUrl = "Foto3.jpg"
      ItemName = "Charmander"
      ItemDescription = "50cm x 60cm"
      ItemPrice = "$12.000"
      stock = "3"
      initial = "1"
      />
    </div>
  </div>
)
    
    
    )

}

export default ItemList;
