import React from 'react';
import './ItemCount.css';
import { useState } from 'react';

const ItemCount = ({stock, initial}) => {

  const [contador, ActualizarContador] = useState(initial);

  const SubirCantidad = () => {
    if (stock > contador){
      ActualizarContador(contador + 1);
      document.getElementById("BotonAgregar").disabled = false;
      document.getElementById("BotonAgregar").style.color = "red";
    }else{
      document.getElementById("BotonAgregar").disabled = true;
      document.getElementById("BotonAgregar").style.color = "grey";
    }
  };
  
  const BajarCantidad = () => {
    if (contador>1){
      ActualizarContador(contador - 1);
      document.getElementById("BotonAgregar").disabled = false;
      document.getElementById("BotonAgregar").style.color = "red";
    }else{
      document.getElementById("BotonAgregar").disabled = true;
      document.getElementById("BotonAgregar").style.color = "grey";
    }
  };

  
  return <div>
      <div className="ContenedorContador">
        <button className = "BotonContadorMas" onClick={SubirCantidad}>+</button>
        <div id = "Contador">{contador}</div>
        <button className = "BotonContadorMenos" onClick={BajarCantidad}>-</button>
      </div>
      <button id = "BotonAgregar">Agregar al carrito</button>
      
  </div>;
}


export default ItemCount;
