import React from 'react';
import ItemCount from '../../components/ItemCount/ItemCount';
import '../Item/Item.css';

const Item = ({producto}) => {

    const Add = (counter) =>{
        alert(`Se agregaron ${counter} productos.`);
      };  

  return (
      
    <div className = "ItemBox">
        
        <img src = {producto.PictureUrl} alt = {producto.ItemName} />
        <p>{producto.ItemName}</p>
        <div>Id de producto: {producto.ItemId}</div>
        <p>Cartel de {producto.ItemName} de {producto.ItemDescription}</p>
        <p>Precio: {producto.ItemPrice}</p>
        <button>Detalles del producto</button>
        <ItemCount
        stock = "5"
        initial = "1"
        Add = {Add}     
        />



    </div>
  )
}

export default Item;
